/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);

var gamepad = gamepads[eventsFunctionContext.getArgument("player_ID") - 1];
if (gamepad == null) {
    //we need keep this condition because when use have not yet plug her controller we can't get the controller in the gamepad variable.
    //and if we delete this condition the game freeze
    return;
}

var param_trigger = eventsFunctionContext.getArgument("trigger").toUpperCase();

switch (param_trigger) {
    case 'LT':
    case 'L2':
        eventsFunctionContext.returnValue = gamepad.buttons[6].value;
        break;

    case 'RT':
    case 'R2':
        eventsFunctionContext.returnValue = gamepad.buttons[7].value;
        break;

    default:
        eventsFunctionContext.returnValue = -1;
        break;
}

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);

var gamepad = gamepads[eventsFunctionContext.getArgument("player_ID") - 1];
if (gamepad == null) return;

var getNormalizedAxisValue = function (v) {
    gdjs._extensionController = gdjs._extensionController || { deadzone: 0.2 };
    
    // Anything smaller than this is assumed to be 0,0
    var DEADZONE = gdjs._extensionController.deadzone;

    if (Math.abs(v) < DEADZONE) {
        // In the dead zone, set to 0
        v = 0;

        if (v == null) {
            return 0;
        } else {
            return v;
        }

    } else {
        // We're outside the dead zone, but we'd like to smooth
        // this value out so it still runs nicely between 0..1.
        // That is, we don't want it to jump suddenly from 0 to
        // DEADZONE.

        // Remap v from
        //    DEADZONE..1 to 0..(1-DEADZONE)
        // or from
        //    -1..-DEADZONE to -(1-DEADZONE)..0

        v = v - Math.sign(v) * DEADZONE;

        // Remap v from
        //    0..(1-DEADZONE) to 0..1
        // or from
        //    -(1-DEADZONE)..0 to -1..0

        return v / (1 - DEADZONE);

    }
};

var stick = eventsFunctionContext.getArgument("stick").toUpperCase();
var direction = eventsFunctionContext.getArgument("direction").toUpperCase();
var axis;

switch (stick) {
    case 'LEFT':
        switch (direction) {
            case 'LEFT':
                if (getNormalizedAxisValue(gamepad.axes[0]) < 0) {
                    eventsFunctionContext.returnValue = -getNormalizedAxisValue(gamepad.axes[0]);
                }
                break;

            case 'RIGHT':
                if (getNormalizedAxisValue(gamepad.axes[0]) > 0) {
                    eventsFunctionContext.returnValue = getNormalizedAxisValue(gamepad.axes[0]);
                }
                break;

            case 'UP':
                if (getNormalizedAxisValue(gamepad.axes[1]) < 0) {
                    eventsFunctionContext.returnValue = -getNormalizedAxisValue(gamepad.axes[1]);
                }
                break;

            case 'DOWN':
                if (getNormalizedAxisValue(gamepad.axes[1]) > 0) {
                    eventsFunctionContext.returnValue = getNormalizedAxisValue(gamepad.axes[1]);
                }
                break;

            default:
                break;
        }
        break;

    case 'RIGHT':
        switch (direction) {
            case 'LEFT':
                if (getNormalizedAxisValue(gamepad.axes[2]) < 0) {
                    eventsFunctionContext.returnValue = -getNormalizedAxisValue(gamepad.axes[2]);
                }
                break;

            case 'RIGHT':
                if (getNormalizedAxisValue(gamepad.axes[2]) > 0) {
                    eventsFunctionContext.returnValue = getNormalizedAxisValue(gamepad.axes[2]);
                }
                break;

            case 'UP':
                if (getNormalizedAxisValue(gamepad.axes[3]) < 0) {
                    eventsFunctionContext.returnValue = -getNormalizedAxisValue(gamepad.axes[3]);
                }
                break;

            case 'DOWN':
                if (getNormalizedAxisValue(gamepad.axes[3]) > 0) {
                    eventsFunctionContext.returnValue = getNormalizedAxisValue(gamepad.axes[3]);
                }
                break;

            default:
                break;
        }
        break;

    default:
        break;
}

var playerId = eventsFunctionContext.getArgument("player_ID") - 1;
if (playerId < 0 || playerId > 4) return;

gdjs._extensionController = gdjs._extensionController || { deadzone: 0.2 };
gdjs._extensionController.players = gdjs._extensionController.players || {};
gdjs._extensionController.players[playerId] = gdjs._extensionController.players[playerId] || { lastButtonUsed: -1 };

eventsFunctionContext.returnValue = gdjs._extensionController.players[playerId].lastButtonUsed;

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);

var playerId = eventsFunctionContext.getArgument("player_ID") - 1;
var gamepad = gamepads[playerId];

if (gamepad == null) return;

var param_button = eventsFunctionContext.getArgument("button").toUpperCase();
var btn_id;

switch (param_button) {
    case 'A':
    case 'CROSS':
        btn_id = 0;
        //console.log('A CROSS');
        break;
    case 'B':
    case 'CIRCLE':
        btn_id = 1;
        //console.log('B CIRCLE');
        break;
    case 'X':
    case 'SQUARE':
        btn_id = 2;
        //console.log('X SQUARE');
        break;
    case 'Y':
    case 'TRIANGLE':
        btn_id = 3;
        //console.log('Y TRIANGLE');
        break;
    case 'LB':
    case 'L1':
        btn_id = 4;
        //console.log('LB L1');
        break;
    case 'RB':
    case 'R1':
        btn_id = 5;
        //console.log('RB R1');
        break;
    case 'LT':
    case 'L2':
        btn_id = 6;
        //console.log('LT L2');
        break;
    case 'RT':
    case 'R2':
        btn_id = 7;
        //console.log('RT R2');
        break;

    case 'UP':
        btn_id = 12;
        //console.log('UP');
        break;
    case 'DOWN':
        btn_id = 13;
        //console.log('DOWN');
        break;
    case 'LEFT':
        btn_id = 14;
        //console.log('LEFT');
        break;
    case 'RIGHT':
        btn_id = 15;
        //console.log('RIGHT');
        break;

    case 'BACK':
    case 'SHARE':
        btn_id = 8;
        //console.log('BACK SHARE');
        break;
    case 'START':
    case 'OPTIONS':
        btn_id = 9;
        //console.log('START OPTIONS');
        break;

    case 'CLICK_STICK_LEFT':
        btn_id = 10;
        //console.log('CLICK_STICK_LEFT');
        break;
    case 'CLICK_STICK_RIGHT':
        btn_id = 11;
        //console.log('CLICK_STICK_RIGHT');
        break;

    //PS4
    case 'PS_BUTTON':
        btn_id = 16;
        //console.log('PS_BUTTON');
        break;
    case 'CLICK_TOUCHPAD':
        btn_id = 17;
        //console.log('CLICK_TOUCHPAD');
        break;

    default:
        //console.log('null btn');
        break;
}

if (!gamepad.buttons[btn_id]) {
    return;
}

gdjs._extensionController = gdjs._extensionController || { deadzone: 0.2 };
gdjs._extensionController.players = gdjs._extensionController.players || {};
gdjs._extensionController.players[playerId] = gdjs._extensionController.players[playerId] || { lastButtonUsed: -1 };

if (gamepad.buttons[btn_id].pressed) {
    gdjs._extensionController.players[playerId].lastButtonUsed = btn_id;
    eventsFunctionContext.returnValue = true;
} else {
    eventsFunctionContext.returnValue = false;
}

var newDeadzone = eventsFunctionContext.getArgument("param_deadzone");
if (newDeadzone < 0 || newDeadzone >= 1) return;

gdjs._extensionController = gdjs._extensionController || { deadzone: 0.2 };

gdjs._extensionController.deadzone = eventsFunctionContext.getArgument("param_deadzone");

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);

var gamepad = gamepads[eventsFunctionContext.getArgument("player_ID") - 1];
if (gamepad == null) return;


var getNormalizedAxisValue = function (v) {
    gdjs._extensionController = gdjs._extensionController || { deadzone: 0.2 };

    // Anything smaller than this is assumed to be 0,0
    var DEADZONE = gdjs._extensionController.deadzone;

    if (Math.abs(v) < DEADZONE) {
        // In the dead zone, set to 0
        v = 0;

        if (v == null) {
            return 0;
        } else {
            return v;
        }

    } else {
        // We're outside the dead zone, but we'd like to smooth
        // this value out so it still runs nicely between 0..1.
        // That is, we don't want it to jump suddenly from 0 to
        // DEADZONE.

        // Remap v from
        //    DEADZONE..1 to 0..(1-DEADZONE)
        // or from
        //    -1..-DEADZONE to -(1-DEADZONE)..0

        v = v - Math.sign(v) * DEADZONE;

        // Remap v from
        //    0..(1-DEADZONE) to 0..1
        // or from
        //    -(1-DEADZONE)..0 to -1..0

        return v / (1 - DEADZONE);
    }
};

var stick = eventsFunctionContext.getArgument("stick").toUpperCase();
var direction = eventsFunctionContext.getArgument("direction").toUpperCase();
var axis;

switch (stick) {
    case 'LEFT':
        switch (direction) {
            case 'LEFT':

                if (getNormalizedAxisValue(gamepad.axes[0]) < 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            case 'RIGHT':
                if (getNormalizedAxisValue(gamepad.axes[0]) > 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            case 'UP':
                if (getNormalizedAxisValue(gamepad.axes[1]) < 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            case 'DOWN':
                if (getNormalizedAxisValue(gamepad.axes[1]) > 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            default:
                break;
        }
        break;

    case 'RIGHT':
        switch (direction) {
            case 'LEFT':
                if (getNormalizedAxisValue(gamepad.axes[2]) < 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            case 'RIGHT':
                if (getNormalizedAxisValue(gamepad.axes[2]) > 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            case 'UP':
                if (getNormalizedAxisValue(gamepad.axes[3]) < 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            case 'DOWN':
                if (getNormalizedAxisValue(gamepad.axes[3]) > 0) {
                    eventsFunctionContext.returnValue = true;
                }
                break;

            default:
                break;
        }
        break;

    default:
        break;
}

if (eventsFunctionContext.returnValue !== true) {
    eventsFunctionContext.returnValue = false;
}

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);

// Gamepads can be disconnected and become null, so we have to filter them.
eventsFunctionContext.returnValue = Object.keys(gamepads).filter(key => !!gamepads[key]).length;

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);
var playerId = eventsFunctionContext.getArgument("player_ID") - 1;

if (gamepads[playerId] && gamepads[playerId].id) {
    eventsFunctionContext.returnValue = gamepads[playerId].id;
} else {
    eventsFunctionContext.returnValue = "empty";
}

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);

var player_ID = eventsFunctionContext.getArgument("player_ID") - 1;
var controller_type = eventsFunctionContext.getArgument("controller_type").toUpperCase();

if (gamepads[player_ID] != null) {
    if (gamepads[player_ID].id.toUpperCase().indexOf(controller_type) != -1) {
        eventsFunctionContext.returnValue = true;
    } else {
        eventsFunctionContext.returnValue = false;
    }
}

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);
var playerId = eventsFunctionContext.getArgument("player_ID") - 1;

// If gamepad was disconnected it will be null (so this will return false)
// If gamepad was never connected it will be undefined (so this will return false)
eventsFunctionContext.returnValue = !!gamepads[playerId];

/** @type {Gamepad[]} */
var gamepads = navigator.getGamepads ? navigator.getGamepads() : (navigator.webkitGetGamepads ? navigator.webkitGetGamepads() : []);

var playerId = eventsFunctionContext.getArgument("player_ID") - 1;
var duration = eventsFunctionContext.getArgument("param_duration") || 1;

if (gamepads[playerId].vibrationActuator) {
  gamepads[playerId].vibrationActuator.playEffect("dual-rumble", {
    startDelay: 0,
    duration: duration * 1000,
    weakMagnitude: 1.0,
    strongMagnitude: 1.0
  });
}
